mapper.select:
SELECT BOARD_IDX
	, BOARD_WRITER
	, BOARD_TITLE
	, BOARD_CONTENTS
	, BOARD_VIEW_COUNT
	, BOARD_WRITE_DATE 
	, FILE_IDX
FROM SAMPLE_BOARD_TB
WHERE DATE_FORMAT(BOARD_WRITE_DATE, "%Y-%m-%d")

	 AND BOARD_IDX         = #{boardIdx}       
	 AND BOARD_WRITER      = #{boardWriter}    
	 AND BOARD_TITLE       = #{boardTitle}     
	 AND BOARD_CONTENTS    = #{boardContents}  
	 AND BOARD_VIEW_COUNT  = #{boardViewCount} 
	 AND BOARD_WRITE_DATE = #{boardWriteDate}  
	 AND FILE_IDX          = #{fileIdx}        

 LIMIT #{offset}, #{row}

SELECT count(1) FROM SAMPLE_BOARD_TB

	 AND BOARD_IDX         = #{boardIdx}       
	 AND BOARD_WRITER      = #{boardWriter}    
	 AND BOARD_TITLE       = #{boardTitle}     
	 AND BOARD_CONTENTS    = #{boardContents}  
	 AND BOARD_VIEW_COUNT  = #{boardViewCount} 
	 AND BOARD_WRITE_DATE = #{boardWriterDate} 
	 AND FILE_IDX          = #{fileIdx}

mapper.insert:
INSERT INTO SAMPLE_BOARD_TB (
	
	 , BOARD_IDX        
	 , BOARD_WRITER     
	 , BOARD_TITLE      
	 , BOARD_CONTENTS   
	 , BOARD_VIEW_COUNT 
	 , BOARD_WRITE_DATE 
	 , FILE_IDX         
	
) VALUES(
	
	 , #{boardIdx}       
	 , #{boardWriter}    
	 , #{boardTitle}     
	 , #{boardContents}  
	 , #{boardViewCount} 
	 , #{boardWriteDate} 
	 , #{fileIdx}        
	
)
 SELECT LAST_INSERT_ID()

mapper.update:
UPDATE SAMPLE_BOARD_TB
	
	 , BOARD_WRITER     = #{boardWriter}    
	 , BOARD_TITLE      = #{boardTitle}     
	 , BOARD_CONTENTS   = #{boardContents}  
	 , BOARD_VIEW_COUNT = #{boardViewCount} 
	 , FILE_IDX         = #{fileIdx}       
	
WHERE BOARD_IDX = #{boardIdx}
