mapper.insert:
INSERT INTO sample_writer_tb(
BOARD_WRITER
, BOARD_WRITER_NAME
, BOARD_WRITER_PW
, BOARD_WRITER_PHONE
, BOARD_WRITER_EMAIL
, FILE_IDX
, BOARD_WRITER_JOIN_DATE
, SALT
) VALUES (
#{boardWriter}
, #{boardWriterName}
, #{boardWriterPw}
, #{boardWriterPhone}
, #{boardWriterEmail}
, #{fileIdx}
, #{boardWriterJoinDate}
, #{salt}
)
 SELECT LAST_INSERT_ID()

mapper.select:
SELECT BOARD_WRITER, BOARD_WRITER_PW
FROM sample_writer_tb 
WHERE BOARD_WRITER = #{id} and BOARD_WRITER_PW = #{pw}

mapper.insert:
	INSERT INTO sample_file_tb(
	FILE_ORIGINAL_NAME
	, FILE_CHANGED_NAME
	, FILE_PATH
	) values
	(
	#{fileOriginalName}
	, #{fileChangedName}
	, #{filePath}
	)
 SELECT LAST_INSERT_ID()

mapper.update:
UPDATE 
	sample_writer_tb 
SET
	BOARD_WRITER_NAME = #{boardWriterName}
	, BOARD_WRITER_PW = #{boardWriterPw}
	, BOARD_WRITER_PHONE = #{boardWriterPhone}
	, BOARD_WRITER_EMAIL = #{boardWriterEmail}
WHERE 
	BOARD_WRITER = #{boardWriter}
